import React, { useEffect, useState }  from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Joul from './Work/Joul';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './WorkContent.css';

function WorkContent(){
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(true)
        } else {
            setButton(false)
        }
    };

    useEffect(() => {
        showButton()
    }, []);

    window.addEventListener('resize', showButton);


    return (
        <>
       <div className="container">
          <div className="semi_title">
             <span className="span_semititle">UI/UX</span>
          </div>
           <div className="content">
               <div className="emptycontent"></div>
                <div className="herocontent">
                <Link to="/joul" className="nav-link" onClick={closeMobileMenu}>
                <span className="span_title">Joul</span>
                </Link> <br />
                   <span className="span_exp">children's toy to learn sustainability</span>
                </div>
                <div className="indexcontent">
                    <span className="index">UX / UI Design</span>
                </div>
            </div>






             <div className="footer"></div>
        </div>

        </>
    );
}

export default WorkContent;
