import React, { useEffect, useState }  from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Joul from './Work/Joul';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './WorkContent.css';

function WorkContent(){
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(true)
        } else {
            setButton(false)
        }
    };

    useEffect(() => {
        showButton()
    }, []);

    window.addEventListener('resize', showButton);


    return (
        <>
       <div className="container">
            <div className="semi_title semi_title_work">
               <span className="span_semititle">UI/UX</span>
            </div>
        </div>

        <div className="container worklist">
         <div className="work1">
            <Link to="/joul" className="nav-link" onClick={closeMobileMenu}>
                <span className="span_title">JOUL, A TOY TO LEARN SUSTAINABILITY THROUGH PLAY</span>
                <img className="work_img" src={"./images/about/work_joul.jpg"} />

            </Link>
         </div>
         <div className="work2 ">
            <Link to="/joul" className="nav-link span_title_2" onClick={closeMobileMenu}>
                <span className="span_title span_title_2">SEAM, SEAMING LOVE AND MEMORY REMOTELY FOR ALZHEIMER'S</span>
                <img className="work_img_2" src={"./images/about/work_joul.jpg"} />
            </Link>
         </div>
         <div className="work3">
            <Link to="/joul" className="nav-link" onClick={closeMobileMenu}>
                <span className="span_title">NORI NURI, INTERACTIVE PLAY EDUCATION PLATFORM</span>
                <img className="work_img_3" src={"./images/about/work_joul.jpg"} />
            </Link>
         </div>
        </div>

        <div className="container">
            <div className="semi_title semi_title_work">
               <span className="span_semititle">DATA DESIGN</span>
            </div>
        </div>

        <div className="container worklist">
         <div className="work1">
            <Link to="/joul" className="nav-link" onClick={closeMobileMenu}>
                <span className="span_title">JOUL, A TOY TO LEARN SUSTAINABILITY THROUGH PLAY</span>
                <img className="work_img" src={"./images/about/work_joul.jpg"} />

            </Link>
         </div>
         <div className="work2">
            <Link to="/joul" className="nav-link" onClick={closeMobileMenu}>
                <span className="span_title">SEAM, SEAMING LOVE AND MEMORY REMOTELY FOR ALZHEIMER INDIVIDUALS</span>
                <img className="work_img" src={"./images/about/work_joul.jpg"} />
            </Link>
         </div>
         <div className="work3">
            <Link to="/joul" className="nav-link" onClick={closeMobileMenu}>
                <span className="span_title">NORI NURI, INTERACTIVE PLAY EDUCATION PLATFORM</span>
                <img className="work_img" src={"./images/about/work_joul.jpg"} />
            </Link>
         </div>
        </div>

        </>
    );
}

export default WorkContent;
